#import sysimport csvimport jsonfilename = 'chs2012-creative-scotland-lot'#filename = 'chs2012-creative-scotland-gia'#filename = 'CHS_2013_Creative_Scotland_awards'with open(filename+'.csv','rU') as f: #r = read, b = binary, U = universal (e.g. multilines)    csvDict = csv.DictReader(f, delimiter=',', dialect='excel')    out = [obj for obj in csvDict]with open(filename+'.json','wb') as f:    json.dump(out,f,ensure_ascii=False)